import { Component, OnInit, AfterViewInit, ViewEncapsulation,ViewChild, ElementRef } from '@angular/core';
import { DemandeRecyclageService } from '../../services/demande-recyclage/demande-recyclage.service';
import { NgForm } from '@angular/forms';
import { CommentaireDemande } from 'src/app/entities/demanderecyclage/commentaire-demande.model';
import { CommentaireDemandeService } from 'src/app/services/demande-recyclage/commentaire-demande.service';
import { CertificatRecyclage } from 'src/app/entities/demanderecyclage/certificat-recyclage.model';
import { DemandeRecyclage } from 'src/app/entities/demanderecyclage/demande-recyclage.model';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import { Etat } from 'src/app/entities/enumerations/etat.model';
declare var bootstrap: any; 
export const LOGO_BASE64 = 'data:image/png;base64,';
@Component({
  selector: 'app-demande-recycle',
  templateUrl: './demande-recycle.component.html',
  styleUrls: ['./demande-recycle.component.css'],
  encapsulation: ViewEncapsulation.None
})
export class DemandeRecycleComponent implements OnInit, AfterViewInit {
  @ViewChild('form') form!: NgForm;
  @ViewChild('fileInput') fileInput!: ElementRef<HTMLInputElement>;

  demandes: DemandeRecyclage[] = [];
  selectedDemande: DemandeRecyclage | null = null;
  commentaires: CommentaireDemande[] = [];
  newComment = '';
  deleteId: number | null = null;
  modal: any;
  today = new Date().toISOString().split('T')[0];

  // form model
  demande: DemandeRecyclage = {
    idDemandeRecyclage: null,
    title: null,
    dateCreationDemandeRecyclage: new Date(),
    descriptionDemandeRecyclage: '',
    nbrAppareils: 0,
    etatDemandeRecyclage: Etat.NONAFFECTEE,
    prixDemandeRecyclage: 0,
    imageData: null,
    imageBase64: null,
    certificatRecyclage: null
  };
  originalDemande!: DemandeRecyclage;
  isEditMode = false;
  imageChanged = false;
  imageLabel = 'Choisir une image';
  wasSubmitted = false;

  constructor(
    private demandeService: DemandeRecyclageService,
    private commentaireService: CommentaireDemandeService
  ) {}

  ngOnInit(): void {
    this.loadDemandes();
  }

  ngAfterViewInit(): void {
    const el = document.getElementById('staticBackdrop');
    if (el) this.modal = new bootstrap.Modal(el);
  }

  private loadDemandes(): void {
    this.demandeService.getAll().subscribe(list => this.demandes = list);
  }

  // open the add/edit modal
  openModal(): void {
    this.modal.show();
  }

  resetForm(): void {
    this.form.resetForm();
    this.demande = {
      idDemandeRecyclage: null,
      title: '',
      dateCreationDemandeRecyclage:null,
      descriptionDemandeRecyclage: '',
      nbrAppareils: 0,
      etatDemandeRecyclage: Etat.NONAFFECTEE,
      prixDemandeRecyclage: 0
    };
    this.wasSubmitted = false;
    this.imageChanged = false;
    this.isEditMode = false;
    this.imageLabel = 'Choisir une image';
  }

  //  Add or Update
  saveOrUpdate(form: NgForm): void {
    this.wasSubmitted = true;
  
    //  Mark all fields as touched so errors appear immediately
    if (!form.valid) {
      Object.values(form.controls).forEach(control => {
        control.markAsTouched();
      });
      return;
    }
  
    if (!this.validateForm()) {
      return;
    }
  
    if (this.isEditMode) {
      this.doUpdate(); // or update()
    } else {
      this.doSave(); // or save()
    }
  }

  validateForm(): boolean {
    const desc = this.demande.descriptionDemandeRecyclage;
    // force a Date object out of whatever came back from the date input
    const selectedDate = this.demande.dateCreationDemandeRecyclage
      ? new Date(this.demande.dateCreationDemandeRecyclage)
      : null;
  
    // midnight today (parsed from yyyy-MM-dd)
    const todayDate = new Date(this.today);
  
    console.log('selectedDate:', selectedDate);
    console.log('selectedDate < todayDate:', selectedDate && selectedDate < todayDate);
  
    return (
      !!desc &&
        desc.trim().length >= 15 &&
      !!selectedDate &&
        selectedDate < todayDate &&
      this.demande.nbrAppareils > 0 &&
      this.demande.prixDemandeRecyclage > 0 &&
      (!!this.demande.imageData || this.isEditMode)
    );
  }

  private doSave(): void {
    const formData = new FormData();
    const dto = { ...this.demande };
    delete (dto as any).imageData;
    formData.append('demande', JSON.stringify(dto));
    if (this.demande.imageData) {
      formData.append('file', this.dataURItoBlob(this.demande.imageData!), 'image.png');
    }
    this.demandeService.save(formData).subscribe(() => {
      this.loadDemandes();
      this.modal.hide();
      this.resetForm();
    });
  }

  private doUpdate(): void {
    const formData = new FormData();
    const dto = { ...this.demande };
    delete (dto as any).imageData;
    formData.append('demande', JSON.stringify(dto));
    if (this.imageChanged && this.demande.imageData) {
      formData.append('file', this.dataURItoBlob(this.demande.imageData), 'image.png');
    }
    this.demandeService.update(formData).subscribe(() => {
      this.loadDemandes();
      this.modal.hide();
      this.resetForm();
    });
  }

  edit(d: DemandeRecyclage): void {
    // clone for comparisons
    this.demande = { ...d };
    if (d.dateCreationDemandeRecyclage) {
      this.demande.dateCreationDemandeRecyclage = new Date(d.dateCreationDemandeRecyclage);
    }
    this.demande.imageData = d.imageBase64 ? 'data:image/png;base64,' + d.imageBase64 : null;
    this.originalDemande = JSON.parse(JSON.stringify(this.demande));
    this.isEditMode = true;
    this.imageChanged = false;
    this.fileInput.nativeElement.value = '';
    this.imageLabel = 'Choisir une image';
    this.openModal();
  }

  delete(id: number | null): void {
        if (id == null) {
         console.warn('Suppression impossible : ID manquant');
          return;
        }
        this.deleteId = id;
    const el = document.getElementById('confirmDeleteModal');
    if (el) new bootstrap.Modal(el).show();
  }

  onFileChange(event: any): void {
    const file = event.target.files[0];
    const maxSizeMB = 100;

    if (file && file.size > maxSizeMB * 1024 * 1024) {
      alert(`Le fichier dpasse la taille maximale autorise (${maxSizeMB}MB).`);
      return;
    }

    if (file) {
      this.imageLabel = file.name;
      const reader = new FileReader();
      reader.onload = () => {
        this.demande.imageData = reader.result as string;
        this.imageChanged = true;
      };
      reader.readAsDataURL(file);
    }
  }

  confirmDelete(): void {
    if (!this.deleteId) return;
    this.demandeService.remove(this.deleteId).subscribe(() => {
      this.loadDemandes();
      this.deleteId = null;
      const el = document.getElementById('confirmDeleteModal');
      bootstrap.Modal.getInstance(el)?.hide();
    });
  }

  viewDetails(d: DemandeRecyclage): void {
    this.selectedDemande = d;
    this.commentaireService.getCommentsByDemandeId(d.idDemandeRecyclage!).subscribe(c => this.commentaires = c);
    const el = document.getElementById('detailsModal');
    if (el) new bootstrap.Modal(el).show();
  }

  submitComment(): void {
    if (!this.newComment.trim() || !this.selectedDemande?.idDemandeRecyclage) return;
    const comment: CommentaireDemande = {
      content: this.newComment,
      user: null,
      dateCreation: new Date(),
      demandeRecyclage: { idDemandeRecyclage: this.selectedDemande.idDemandeRecyclage! }
    };
    this.commentaireService.addComment(comment).subscribe(c => {
      this.commentaires.push(c);
      this.newComment = '';
    });
  }

  deleteComment(id: number): void {
    if (!confirm('Voulez-vous supprimer ce commentaire ?')) return;
    this.commentaireService.deleteComment(id).subscribe(() => {
      this.commentaires = this.commentaires.filter(c => c.idCommentaire !== id);
    });
  }

  downloadCertificat(demande: DemandeRecyclage): void {
    if (!demande.idDemandeRecyclage) {
      alert('ID invalide');
      return;
    }
    this.demandeService.getCertificatByDemandeId(demande.idDemandeRecyclage)
      .subscribe((cert: CertificatRecyclage) => {
        if (!cert.dateEmissionCertificat) {
          alert('Aucun certificat');
          return;
        }
        const doc = new jsPDF({ unit: 'pt', format: 'a4' });
        const w = doc.internal.pageSize.getWidth();

        // header
        doc.setFillColor('#68b484');
        doc.rect(0, 0, w, 40, 'F');
        doc.addImage(LOGO_BASE64, 'PNG', 40, 50, 60, 60);

        // title
        const titleY = 50 + 60 + 20;
        doc.setFontSize(22);
        doc.setTextColor('#000');
        doc.text('CERTIFICAT DE RECYCLAGE', w/2, titleY, { align: 'center' });

        // image
        let y = titleY + 20;
        if (demande.imageBase64) {
          const imgProps = doc.getImageProperties(demande.imageBase64);
          const imgW = 300;
          const imgH = imgProps.height / imgProps.width * imgW;
          doc.addImage('data:image/png;base64,' + demande.imageBase64, 'PNG', (w-imgW)/2, y, imgW, imgH);
          y += imgH + 20;
        }

        // rows
        const left = 40, lh=20;
        const creation = demande.dateCreationDemandeRecyclage
          ? new Date(demande.dateCreationDemandeRecyclage).toLocaleDateString('fr-FR')
          : '';
        const emission = new Date(cert.dateEmissionCertificat)
          .toLocaleString('fr-FR', { dateStyle:'short', timeStyle:'medium' });

        const drawRow = (label: string, value: string) => {
          doc.setFont('helvetica','bold').setTextColor('#000').setFontSize(12).text(label, left, y);
          doc.setFont('helvetica','normal').setTextColor('#c00').text(value, left+150, y, { maxWidth: w-left-150-40 });
          y += lh;
        };
        drawRow('Titre      :', demande.title|| '');

        drawRow('Description          :', demande.descriptionDemandeRecyclage || '');
        drawRow('Nombre dappareils    :', `${demande.nbrAppareils}`);
        drawRow('Prix                  :', `${demande.prixDemandeRecyclage.toFixed(2)} DT`);
        drawRow('Date de cration      :', creation);
        drawRow('Date dmission       :', emission);

        // signature on the right
        y += 60;
        const sigW=200, sigX=w-40-sigW;
        doc.setLineWidth(0.5).line(sigX, y, sigX+sigW, y);
        const sigLabel='Signature';
        const tw = doc.getTextWidth(sigLabel);
        doc.setFont('helvetica','normal').setFontSize(12).text(sigLabel, sigX+(sigW-tw)/2, y+15);

        doc.save(`certificat_${demande.idDemandeRecyclage}.pdf`);
      },
      err => {
        console.error(err);
        alert('Erreur rcupration certificat');
      });
  }

  private dataURItoBlob(dataURI: string): Blob {
    const byteString = atob(dataURI.split(',')[1]);
    const arr = new Uint8Array(byteString.length);
    for (let i=0; i<byteString.length; i++) arr[i]=byteString.charCodeAt(i);
    return new Blob([arr], { type: 'image/png' });
  }


}
